Card Layout.css:
.card-container {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
}
.card {
  background-color: #f9f9f9;
  padding: 20px;
}

Responsive Navbar.css:
.navbar {
  display: flex;
  justify-content: space-around;
}

@media (max-width: 767px) {
  .navbar {
    display: none; /* Hide on small screens */
  }
  .hamburger-menu {
    display: block;
  }
}

Full Screen Hero Layout.css:
.hero {
  height: 100vh; /* Full viewport height */
  background: url('hero-image.jpg') no-repeat center center;
  background-size: cover;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
}

Sidebar Navigation Layout.css:
.wrapper {
  display: flex;
}

.sidebar {
  width: 250px;
  background-color: #333;
  height: 100vh;
  color: white;
  padding: 20px;
  position: fixed;
}

.content {
  margin-left: 250px;
  padding: 20px;
}

Split Screen Layout.css:
.split {
  display: flex;
  height: 100vh;
}

.left, .right {
  width: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.left {
  background-color: #f4f4f4;
}

.right {
  background-color: #333;
  color: white;
}

Magazine Layout.css:
.magazine-layout {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr;
  gap: 20px;
}

.main-article {
  grid-column: 1 / span 2;
  background-color: #f4f4f4;
  padding: 20px;
}

.side-article {
  background-color: #ddd;
  padding: 20px;
}

Sticky Footer Layout.css:
.wrapper {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
}

main {
  flex-grow: 1;
}

.footer {
  background-color: #333;
  color: white;
  text-align: center;
  padding: 10px;
}

Off Canvas Menu Layout.css:
.off-canvas-wrapper {
  display: flex;
  overflow-x: hidden;
}

.off-canvas-menu {
  width: 250px;
  background-color: #333;
  color: white;
  height: 100vh;
  position: absolute;
  left: -250px;
  transition: left 0.3s ease;
}

.content {
  flex-grow: 1;
  padding: 20px;
}

#menu-toggle:checked ~ .off-canvas-menu {
  left: 0;
}

Center-Content Layout.css:
.centered-content {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  text-align: center;
}

Two-Column Layout.css:
:root {
  --SlightGrey: #8d8d8d;
  --MidGrey:    #616161;
  --DarkGrey:  #2b2b2b;

}

body {
  margin-top: 0px;
  margin-bottom: 0px;
}
.container {
    display: flex;
    height: 900px;
    background-color: var(--SlightGrey);
  }
.header {
  flex-direction: column;

}

  .content {

    flex: 3; /* Main content takes 3/4 of the width */
    background-color: var(--MidGrey);

  }
  .sidebar {
    background-color: var(--DarkGrey);
    flex: 1; /* Sidebar takes 1/4 of the width */
  }

Three-Column Layout.css:
:root {
  --lightgrey: #8e8e8e;
  --darkgrey:  #636363;
}

.container {
  height: 900px;
  display: flex;
}
.left-sidebar, .right-sidebar {
  background-color: var(--darkgrey);
  flex: 1; /* Each sidebar takes 1/4 */
}
.content {
  background-color: var(--lightgrey);
  flex: 2; /* Main content takes 2/4 */
}
